import React, { Component } from "react";

export const BookContext = React.createContext();

export class BookProvider extends Component {
    constructor(props) {
        super(props);

        this.state = {
            books: [],
            Users: []
        };
    }

    render() {
        return (
            <CartContext.Provider 
                value={{
                    books: this.state.books,
                    users: this.state.users
                }}
            >
                {this.props.children}
            </CartContext.Provider>
        )
    }
}
