import React, { Component } from 'react';
import { 
    Container 
} from 'reactstrap';
import { useLocation } from "react-router-dom";

import BookContext from '../Contexts/Book';

import 'bootstrap/dist/css/bootstrap.min.css';
import '../Styles/Login.css';

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

function onValidate() {
    let query = useQuery();
    let name = query.get('name');
    let password = query.get('password');

    <BookContext.Comsumer>
        {({ users }) => {
            var user = users.find(user => user.name === name);
            if (!user) {
                this.setState({
                    errors: this.state.errors.concat('User is not exist')
                });
            }
        
            if (user.password !== password) {
                this.setState({
                    errors: this.state.errors.concat('Wrong password')
                });
            }
        }}
    </BookContext.Comsumer>
}

export default class Login extends Component() {
    constructor(props){
        super(props);

        this.state = {
            errors: []
        };
    }

    render() { 
        return (
            <Container>
                <form>
                    <div><h2>Account Login</h2></div>
                    <div>
                        <label for="name">User Name</label><br />
                        <input type="text" id="name" name="name"></input>
                    </div>
                    <div>
                        <label for="password">Password</label><br />
                        <input type="password" id="password" name="password"></input>
                    </div>
                    <div>
                        <button onClick={onValidate.bind(this)}>Login</button>
                    </div>
                    <div>
                        <span>Are you new? </span>
                        <a href="#">Sign Up</a>
                    </div>
                </form>
            </Container>
        );
    }
}
