import React, { Component } from 'react';
import axios from 'axios';
import {
    Container,
    Row,
    Col,
    Card,
    CardImg,
    CardText,
    CardBody,
    CardTitle,
    Button
  } from "reactstrap";

import 'bootstrap/dist/css/bootstrap.min.css';

export default class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            books: []
        };
    }

    componentDidMount() {
        axios.get("http://localhost:5000/api/books").then(res => {
          this.setState({
            books: res.data
          });
        });
    }

    render() {
        const { books } = this.state;
        console.log(books[0]);
        return (
            <Container>
                <h2>The most read book</h2>
                <Row>
                    { books.map(book => (
                        <Col sm="4" margin="5">
                            {/* <Card>
                                <CardImg top width="100%" src={books.image} />
                                <CardBody>
                                    <CardTitle>{books.name}</CardTitle>
                                </CardBody>
                            </Card> */}
                            <img src="https://picsum.photos/320/240" />
                        </Col>
                    ))}
                </Row>
            </Container>
        );   
    }
}
