    //     };

    //     this.redirect = false;

    //     this.onChangeName = this.onChangeName.bind(this);
    //     this.onChangePassword = this.onChangePassword.bind(this);
    //     this.onSubmit = this.onSubmit.bind(this);
    // }

    // onChangeName(e) {
    //     this.setState({
    //         user: [
    //             {name: e.target.value, isValid: false}, 
    //             ...this.state.user
    //         ]
    //     });
    // }

    // onChangePassword(e) {
    //     this.setState({
    //         user: [
    //             {password: e.target.value, isValid: false}, 
    //             ...this.state.user
    //         ]
    //     });
    // }

    // onSubmit(e) {
    //     e.preventDefault();

    //     const { user } = this.state;
    //     const { users } = this.props;

    //     var checkUser = users.find(userItem => userItem.name === user[0].name);

    //     if (!checkUser) {
    //         this.setState({
    //             user: [
    //                 ...user,
    //                 {name: user[0].name, isValid: true}
    //             ]
    //         });
    //         return;
    //     }

    //     if (user[1].password !== checkUser.password) {
    //         this.setState({
    //             user: [
    //                 ...user,
    //                 {password: user[1].password, isValid: true}
    //             ]
    //         });
    //         return;
    //     }

    //     this.redirect = true;
    // }


    return (
        <Container>
            <form>
                <div><h2>Account Login</h2></div>
                { name && <Validate />}
                <div>
                    <label for="name">User Name</label><br />
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        required 
                    />
                </div>
                <div>
                    <label for="password">Password</label><br />
                    <input 
                        type="password" 
                        id="password" 
                        name="password" 
                        required 
                    />
                </div>
                <div>
                    <button>
                        <span>Login</span>
                    </button>
                </div>
                <div>
                    <span>Are you new? </span>
                    <a href="/create">Sign Up</a>
                </div>
            </form>
        </Container>
    );
}

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

function Validate() {
    let query = useQuery();
    let name = query.get('name');
    let password = query.get('password');

    return (
        <div>
            <BookContext.Consumer>
                {({ users }) => {
                    var user = users.find(user => user.name === name);
                    if (!user) {
                        return <Alert color="danger">User is not exist</Alert>
                    }

                    if (user.password !== password) {
                        return <Alert color="danger">Wrong password</Alert>
                    }  

                    var token = jwt.sign({
                        name: name,
                        password: password
                    }, 'secretToken', { expiresIn: '1h' });

                    localStorage.setItem('token', token);

                    return <Redirect to="/" />
                }}
            </BookContext.Consumer>
        </div>
    );
}
