import React from 'react';
import { 
    Container,
    Alert
} from 'reactstrap';
import { useLocation, Redirect } from "react-router-dom";

import 'bootstrap/dist/css/bootstrap.min.css';
import '../Styles/CreateUser.css';

export default function() {
    let query = useQuery();
    let name = query.get('name');
    let password = query.get('password');

    return (
        <Container>
            <form>
                <div>
                    <h2>Sign Up</h2>
                    <p>Please fill in this form to create an account!</p>
                </div>
                <Validate />
                <div>
                    <label for="name">User Name</label><br />
                    <input type="text" id="name" name="name"></input>
                </div>
                <div>
                    <label for="password">Password</label><br />
                    <input type="password" id="password" name="password"></input>
                </div>
                <div>
                    <button>Sign Up</button>
                </div>
            </form>
        </Container>
    );
}

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

function Validate() {
    let query = useQuery();
    let name = query.get('name');
    let password = query.get('password');

    if (!name && !password) {
        return (
            <div>
                <Redirect to="/login" />
                <Alert color="danger">User is not validate</Alert>
                <Alert color="danger">Password is not validate</Alert>
            </div>
        );
    }

    if (!name) {
        return <Alert color="danger">User is not validate</Alert>
    }

    if (!password) {
        return <Alert color="danger">Password is not validate</Alert>
    }
}
